body.script:
document.addEventListener('DOMContentLoaded', function() {
  const themeToggle = document.getElementById("themeToggle");
  const searchToggle = document.getElementById("searchToggle");
  const searchBox = document.getElementById("searchBox");
  const burger = document.getElementById("burger");
  const navList = document.querySelector(".nav ul");
  const iconsContainer = document.querySelector(".icons");

  // ===== تبديل الثيم (الفاتح/الداكن) =====
  themeToggle.addEventListener("click", () => {
    document.body.classList.toggle("dark");
    const icon = themeToggle.querySelector("i");
    if (document.body.classList.contains("dark")) {
      icon.classList.remove("fa-moon");
      icon.classList.add("fa-sun");
    } else {
      icon.classList.remove("fa-sun");
      icon.classList.add("fa-moon");
    }
  });

  // ===== القائمة الجانبية (Side Menu) =====
  (function(){
    const items = Array.from(document.querySelectorAll('.menu-item'));
    items.forEach((btn) => {
      btn.addEventListener('click', () => {
        items.forEach(i => i.classList.remove('active'));
        btn.classList.add('active');
        handleMenuAction(btn.dataset.key);
      });
    });
    function handleMenuAction(action) {
      if (action === 'home') {
        window.scrollTo({ top: 0, behavior: 'smooth' });
      } else if (action === 'search') {
        document.getElementById('searchOverlay').classList.add('show');
        document.getElementById('overlaySearchInput').focus();
      }
    }
  })();

  // ===== البحث التفاعلي في الهيدر =====
  searchToggle.addEventListener("click", (e) => {
    e.stopPropagation();
    iconsContainer.classList.toggle("search-active");
    if (iconsContainer.classList.contains("search-active")) {
      searchBox.querySelector("input").focus();
    }
  });

  document.addEventListener("click", (e) => {
    if (!iconsContainer.contains(e.target)) {
      iconsContainer.classList.remove("search-active");
    }
  });

  // ===== قائمة البرجر (للموبايل) =====
  burger.addEventListener("click", () => {
    navList.classList.toggle("open");
    burger.classList.toggle("active");
  });

  document.querySelectorAll(".nav a").forEach(link => {
    link.addEventListener("click", () => {
      navList.classList.remove("open");
      burger.classList.remove("active");
    });
  });

  // ===== وظائف أخرى =====
  document.getElementById("year").textContent = new Date().getFullYear();

  let lastScrollTop = 0;
  window.addEventListener("scroll", function() {
    const scrollTop = window.pageYOffset || document.documentElement.scrollTop;
    const header = document.querySelector(".header");
    if (scrollTop > lastScrollTop && scrollTop > 100) {
      header.style.transform = "translateY(-100%)";
    } else {
      header.style.transform = "translateY(0)";
    }
    lastScrollTop = scrollTop <= 0 ? 0 : scrollTop;
  }, false);

  // ===== وظائف المفضلة =====
  (function() {
    const wishlist = JSON.parse(localStorage.getItem('wishlist')) || [];
    function updateWishlistUI() {
      document.querySelectorAll('.wishlist-btn').forEach(btn => {
        const productCard = btn.closest('.product-card');
        if (!productCard) return;
        const productId = productCard.querySelector('a').getAttribute('href');
        if (wishlist.includes(productId)) {
          btn.classList.add('active');
          btn.innerHTML = '';
        } else {
          btn.classList.remove('active');
          btn.innerHTML = '';
        }
      });
    }
    document.querySelectorAll('.wishlist-btn').forEach(btn => {
      btn.addEventListener('click', (e) => {
        e.stopPropagation();
        const productCard = btn.closest('.product-card');
        if (!productCard) return;
        const productId = productCard.querySelector('a').getAttribute('href');
        const index = wishlist.indexOf(productId);
        if (index > -1) {
          wishlist.splice(index, 1);
        } else {
          wishlist.push(productId);
        }
        localStorage.setItem('wishlist', JSON.stringify(wishlist));
        updateWishlistUI();
      });
    });
    updateWishlistUI();
  })();

  // ===== نافذة البحث الشفافة (Overlay) =====
  (function() {
    const searchOverlay = document.getElementById('searchOverlay');
    const overlaySearchInput = document.getElementById('overlaySearchInput');
    const closeSearchOverlay = document.getElementById('closeSearchOverlay');
    closeSearchOverlay.addEventListener('click', () => {
      searchOverlay.classList.remove('show');
    });
    searchOverlay.addEventListener('click', (e) => {
      if (e.target === searchOverlay) {
        searchOverlay.classList.remove('show');
      }
    });
    document.addEventListener('keydown', (e) => {
      if (e.key === 'Escape' && searchOverlay.classList.contains('show')) {
        searchOverlay.classList.remove('show');
      }
    });
  })();

  // ===== عدادات الأرقام المتحركة =====
  const statNumbers = document.querySelectorAll('.stat-number');
  if(statNumbers.length > 0) {
    const speed = 200;
    const startCounting = (stat) => {
      const target = +stat.getAttribute('data-target');
      const count = +stat.innerText;
      const increment = target / speed;
      const updateCount = () => {
        const currentCount = +stat.innerText;
        if (currentCount < target) {
          stat.innerText = Math.ceil(currentCount + increment);
          setTimeout(updateCount, 10);
        } else {
          stat.innerText = target;
        }
      };
      updateCount();
    };
    const observerOptions = { threshold: 0.7 };
    const observer = new IntersectionObserver((entries, observer) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          startCounting(entry.target);
          observer.unobserve(entry.target);
        }
      });
    }, observerOptions);
    statNumbers.forEach(stat => observer.observe(stat));
  }

  // ===== زر العودة للأعلى =====
  const backToTopButton = document.getElementById("backToTop");
  window.addEventListener("scroll", () => {
    if (window.pageYOffset > 300) {
      backToTopButton.classList.add("show");
    } else {
      backToTopButton.classList.remove("show");
    }
  });
  backToTopButton.addEventListener("click", () => {
    window.scrollTo({ top: 0, behavior: "smooth" });
  });

  // ===== تبويبات التفاصيل (صفحة المنتج) =====
  const tabBtns = document.querySelectorAll('.tab-btn');
  const tabPanes = document.querySelectorAll('.tab-pane');
  if(tabBtns.length > 0) {
    tabBtns.forEach(btn => {
      btn.addEventListener('click', () => {
        const targetTab = btn.getAttribute('data-tab');
        tabBtns.forEach(b => b.classList.remove('active'));
        tabPanes.forEach(p => p.classList.remove('active'));
        btn.classList.add('active');
        document.getElementById(targetTab).classList.add('active');
      });
    });
  }

  // ===== نسخ الرابط (محسّن) =====
  const copyLinkBtn = document.querySelector('.copy-link-btn');
  if(copyLinkBtn) {
    copyLinkBtn.addEventListener('click', () => {
      const url = copyLinkBtn.dataset.url;
      if (!navigator.clipboard) {
        // Fallback for older browsers
        const textArea = document.createElement("textarea");
        textArea.value = url;
        document.body.appendChild(textArea);
        textArea.select();
        document.execCommand('copy');
        document.body.removeChild(textArea);
        alert('تم نسخ الرابط!');
      } else {
        navigator.clipboard.writeText(url).then(() => {
          alert('تم نسخ الرابط!');
        }).catch(err => {
          console.error('Failed to copy: ', err);
          alert('فشل نسخ الرابط، يرجى النسخ يدوياً.');
        });
      }
    });
  }
});
